  Lorem ipsum dolor sit amet, consectetur adipisicing elit. //右栏

.three-column {
  margin-left: 120px;            //给左栏留位置
  margin-right: 220px;        //给右栏留位置
}
.three-column div:nth-child(1) {
  float: left;
  width: 100%;                    //中间模块占 100%宽度
  height: 300px;
}
.three-column div:nth-child(2){
  position: relative;
  width: 100px;
  height: 300px;
